import React, { useState, useEffect } from 'react';
import FilterBar from '../components/FilterBar'; // Import your FilterBar
// import PizzaCard from '../components/PizzaCard'; // Assuming you have a card component

const Menu = () => {
  // 1. Store the full list of pizzas (never change this after loading)
  const [allPizzas, setAllPizzas] = useState([]);

  // 2. Store the filtered list (this is what you display)
  const [displayedPizzas, setDisplayedPizzas] = useState([]);

  // Simulate fetching data (Replace this with your actual API call)
  useEffect(() => {
    // Example data
    const mockData = [
      { id: 1, name: 'Margherita', category: 'Vegetarian', price: 250 },
      { id: 2, name: 'Pepperoni', category: 'Non-Vegetarian', price: 450 },
      { id: 3, name: 'Veggie Supreme', category: 'Vegetarian', price: 350 },
      { id: 4, name: 'Seafood Delight', category: 'Seafood', price: 600 },
    ];
    setAllPizzas(mockData);
    setDisplayedPizzas(mockData); // Initially display everything
  }, []);

  // 3. THE FILTERING LOGIC
  // This function receives the object { search, category, minPrice, maxPrice } from FilterBar
  const handleFilter = (filters) => {
    let result = [...allPizzas]; // Start with a fresh copy of all data

    // Filter by Search Text
    if (filters.search) {
      result = result.filter((pizza) =>
        pizza.name.toLowerCase().includes(filters.search.toLowerCase())
      );
    }

    // Filter by Category
    if (filters.category) {
      result = result.filter((pizza) => pizza.category === filters.category);
    }

    // Filter by Min Price (Check if not undefined)
    if (filters.minPrice !== undefined && filters.minPrice !== '') {
      result = result.filter((pizza) => pizza.price >= filters.minPrice);
    }

    // Filter by Max Price (Check if not undefined)
    if (filters.maxPrice !== undefined && filters.maxPrice !== '') {
      result = result.filter((pizza) => pizza.price <= filters.maxPrice);
    }

    // Update the UI
    setDisplayedPizzas(result);
  };

  return (
    <div className="menu-page">
      {/* Pass the handler to the FilterBar */}
      <FilterBar onFilter={handleFilter} />

      <div className="pizza-grid" style={{ display: 'flex', flexWrap: 'wrap', gap: '20px', padding: '20px' }}>
        {displayedPizzas.length > 0 ? (
          displayedPizzas.map((pizza) => (
            // Replace this div with your <PizzaCard key={pizza.id} pizza={pizza} />
            <div key={pizza.id} style={{ border: '1px solid #ddd', padding: '10px', borderRadius: '8px' }}>
              <h3>{pizza.name}</h3>
              <p>Category: {pizza.category}</p>
              <p>Price: â‚¹{pizza.price}</p>
            </div>
          ))
        ) : (
          <p>No pizzas found matching your filters.</p>
        )}
      </div>
    </div>
  );
};

export default Menu;
