import React, { useState } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { useHistory } from 'react-router-dom';
import { placeOrder } from '../services/api';
import { clearCart } from '../redux/cartSlice';
import './Checkout.css';

const Checkout = () => {
    const history = useHistory();
    const dispatch = useDispatch();
    const { items, totalPrice } = useSelector(state => state.cart);
    
    const [currentStep, setCurrentStep] = useState(1);
    const [formData, setFormData] = useState({
        name: '',
        contactNo: '',
        address: '',
        paymentMethod: 'Online'
    });
    const [loading, setLoading] = useState(false);
    const [error, setError] = useState('');

    const totalWithTax = totalPrice + (totalPrice * 0.1);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    const handleNextStep = () => {
        if (currentStep === 1 && !formData.name) {
            setError('Please enter your name');
            return;
        }
        if (currentStep === 2 && !formData.contactNo) {
            setError('Please enter your contact number');
            return;
        }
        if (currentStep === 3 && !formData.address) {
            setError('Please enter your address');
            return;
        }
        setError('');
        setCurrentStep(currentStep + 1);
    };

    const handlePreviousStep = () => {
        setError('');
        setCurrentStep(currentStep - 1);
    };

    const handlePlaceOrder = async () => {
        if (!formData.paymentMethod) {
            setError('Please select a payment method');
            return;
        }

        setLoading(true);
        setError('');

        try {
            const orderData = {
                customerName: formData.name,
                contactNo: formData.contactNo,
                address: formData.address,
                paymentMethod: formData.paymentMethod,
                items: items.map(item => ({
                    pizzaId: item._id,
                    pizzaName: item.name,
                    quantity: item.quantity,
                    price: item.price
                })),
                totalAmount: Math.round(totalWithTax)
            };

            const response = await placeOrder(orderData);
            
            if (response) {
                // Clear the cart
                dispatch(clearCart());
                
                // Show success message
                alert('Order placed successfully!');
                
                // Redirect to orders page
                navigate('/orders');
            }
        } catch (err) {
            setError('Failed to place order. Please try again.');
            console.error('Order error:', err);
        } finally {
            setLoading(false);
        }
    };

    if (items.length === 0) {
        return (
            <div className="checkout-container">
                <div className="empty-checkout">
                    <h2>Your cart is empty</h2>
                    <p>Add some pizzas to your cart before checkout</p>
                    <button onClick={() => history.push('/menu')} className="btn-continue-shopping">
                        Continue Shopping
                    </button>
                </div>
            </div>
        );
    }

    return (
        <div className="checkout-container">
            <h1>Checkout</h1>

            {/* Step Indicator */}
            <div className="step-indicator">
                <div className={`step ${currentStep >= 1 ? 'active' : ''}`}>
                    <span className="step-number">1</span>
                    <span className="step-label">Contact</span>
                </div>
                <div className={`step ${currentStep >= 2 ? 'active' : ''}`}>
                    <span className="step-number">2</span>
                    <span className="step-label">Address</span>
                </div>
                <div className={`step ${currentStep >= 3 ? 'active' : ''}`}>
                    <span className="step-number">3</span>
                    <span className="step-label">Payment</span>
                </div>
                <div className={`step ${currentStep >= 4 ? 'active' : ''}`}>
                    <span className="step-number">4</span>
                    <span className="step-label">Review</span>
                </div>
            </div>

            <div className="checkout-content">
                {/* Order Summary Sidebar */}
                <div className="order-summary">
                    <h3>Order Summary</h3>
                    <div className="summary-items">
                        {items.map(item => (
                            <div key={item._id} className="summary-item">
                                <span>{item.name} x {item.quantity}</span>
                                <span>₹{item.price * item.quantity}</span>
                            </div>
                        ))}
                    </div>
                    <div className="summary-breakdown">
                        <div className="breakdown-row">
                            <span>Subtotal:</span>
                            <span>₹{totalPrice}</span>
                        </div>
                        <div className="breakdown-row">
                            <span>Tax (10%):</span>
                            <span>₹{(totalPrice * 0.1).toFixed(0)}</span>
                        </div>
                        <div className="breakdown-row total">
                            <span>Total:</span>
                            <span>₹{Math.round(totalWithTax)}</span>
                        </div>
                    </div>
                </div>

                {/* Form Steps */}
                <div className="checkout-form">
                    {error && <div className="error-message">{error}</div>}

                    {/* Step 1: Contact Information */}
                    {currentStep === 1 && (
                        <div className="form-step">
                            <h2>Contact Information</h2>
                            <div className="form-group">
                                <label>Full Name:</label>
                                <input
                                    type="text"
                                    name="name"
                                    value={formData.name}
                                    onChange={handleInputChange}
                                    placeholder="Enter your full name"
                                    required
                                />
                            </div>
                        </div>
                    )}

                    {/* Step 2: Address */}
                    {currentStep === 2 && (
                        <div className="form-step">
                            <h2>Delivery Address</h2>
                            <div className="form-group">
                                <label>Address:</label>
                                <textarea
                                    name="address"
                                    value={formData.address}
                                    onChange={handleInputChange}
                                    placeholder="Enter your delivery address"
                                    rows="4"
                                    required
                                />
                            </div>
                        </div>
                    )}

                    {/* Step 3: Payment */}
                    {currentStep === 3 && (
                        <div className="form-step">
                            <h2>Contact Number</h2>
                            <div className="form-group">
                                <label>Contact Number:</label>
                                <input
                                    type="tel"
                                    name="contactNo"
                                    value={formData.contactNo}
                                    onChange={handleInputChange}
                                    placeholder="Enter your contact number"
                                    required
                                />
                            </div>
                        </div>
                    )}

                    {/* Step 4: Review & Payment Method */}
                    {currentStep === 4 && (
                        <div className="form-step">
                            <h2>Payment Method</h2>
                            <div className="form-group">
                                <label>Select Payment Method:</label>
                                <div className="payment-options">
                                    <label className="payment-option">
                                        <input
                                            type="radio"
                                            name="paymentMethod"
                                            value="Online"
                                            checked={formData.paymentMethod === 'Online'}
                                            onChange={handleInputChange}
                                        />
                                        <span>Online Payment</span>
                                    </label>
                                    <label className="payment-option">
                                        <input
                                            type="radio"
                                            name="paymentMethod"
                                            value="Cash"
                                            checked={formData.paymentMethod === 'Cash'}
                                            onChange={handleInputChange}
                                        />
                                        <span>Cash on Delivery</span>
                                    </label>
                                    <label className="payment-option">
                                        <input
                                            type="radio"
                                            name="paymentMethod"
                                            value="Card"
                                            checked={formData.paymentMethod === 'Card'}
                                            onChange={handleInputChange}
                                        />
                                        <span>Debit/Credit Card</span>
                                    </label>
                                </div>
                            </div>

                            <div className="review-section">
                                <h3>Order Review</h3>
                                <p><strong>Name:</strong> {formData.name}</p>
                                <p><strong>Contact:</strong> {formData.contactNo}</p>
                                <p><strong>Address:</strong> {formData.address}</p>
                                <p><strong>Payment Method:</strong> {formData.paymentMethod}</p>
                            </div>
                        </div>
                    )}

                    {/* Action Buttons */}
                    <div className="form-buttons">
                        {currentStep > 1 && (
                            <button 
                                onClick={handlePreviousStep}
                                className="btn-secondary"
                                disabled={loading}
                            >
                                Previous
                            </button>
                        )}
                        
                        {currentStep < 4 && (
                            <button 
                                onClick={handleNextStep}
                                className="btn-primary"
                                disabled={loading}
                            >
                                Next
                            </button>
                        )}
                        
                        {currentStep === 4 && (
                            <button 
                                onClick={handlePlaceOrder}
                                className="btn-place-order"
                                disabled={loading}
                            >
                                {loading ? 'Placing Order...' : 'Place Order'}
                            </button>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default Checkout;
