import React, { useState } from 'react';
import './FilterBar.css';

const FilterBar = ({ onFilter }) => {
  const [search, setSearch] = useState('');
  const [category, setCategory] = useState('');
  const [minPrice, setMinPrice] = useState('');
  const [maxPrice, setMaxPrice] = useState('');

  const handleFilter = () => {
    const filters = {
      search: search.trim() || undefined, // Removes accidental spaces
      category: category || undefined,
      // Check specifically for empty string to allow "0" as a valid price
      minPrice: minPrice !== '' ? parseFloat(minPrice) : undefined,
      maxPrice: maxPrice !== '' ? parseFloat(maxPrice) : undefined,
    };
    onFilter(filters);
  };

  const handleReset = () => {
    setSearch('');
    setCategory('');
    setMinPrice('');
    setMaxPrice('');
    onFilter({});
  };

  // Allows pressing "Enter" inside inputs to trigger the filter
  const handleKeyDown = (e) => {
    if (e.key === 'Enter') {
      handleFilter();
    }
  };

  return (
    <div className="filter-wrapper">
      <div className="filter-container">
        
        {/* Search Input */}
        <div className="filter-item search-box">
          <span className="filter-icon">üîç</span>
          <input
            type="text"
            placeholder="Search pizza name..."
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            onKeyDown={handleKeyDown} 
          />
        </div>

        {/* Category Select */}
        <div className="filter-item">
          <select value={category} onChange={(e) => setCategory(e.target.value)}>
            <option value="">All Categories</option>
            <option value="Vegetarian">Vegetarian</option>
            <option value="Non-Vegetarian">Non-Vegetarian</option>
            <option value="Seafood">Seafood</option>
            <option value="Specialty">Specialty</option>
          </select>
        </div>

        {/* Price Inputs */}
        <div className="filter-item price-inputs">
          <input
            type="number"
            placeholder="Min ‚Çπ"
            value={minPrice}
            onChange={(e) => setMinPrice(e.target.value)}
            onKeyDown={handleKeyDown}
          />
          <span className="price-dash">-</span>
          <input
            type="number"
            placeholder="Max ‚Çπ"
            value={maxPrice}
            onChange={(e) => setMaxPrice(e.target.value)}
            onKeyDown={handleKeyDown}
          />
        </div>

        {/* Action Buttons */}
        <div className="filter-buttons">
          <button onClick={handleFilter} className="btn-apply-small">Apply</button>
          <button onClick={handleReset} className="btn-reset-small">Reset</button>
        </div>
      </div>
    </div>
  );
};

export default FilterBar;
